import React, { useEffect, useState, useCallback } from "react";
import css from "./App.module.css";
import BarLoader from "react-spinners/BarLoader";
import TypewriterComponent from "typewriter-effect";

function App() {
  const [loading, setLoading] = useState(false);
  const [barWidth, setBarWidth] = useState(150);
  const [showHello, setShowHello] = useState(false);

  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setTimeout(() => {
        setBarWidth(150);
      }, 7000);
    }, 8000);
    setTimeout(() => {
      setBarWidth(window.innerWidth);
    }, 7400);
  }, []);

  useEffect(() => {
    if (!loading) {
      setTimeout(() => {
        setShowHello(true);
      }, 1000);
    }
  }, [loading]);

  return (
    <div>
      {loading ? (
        <>
          <p className={css.introName}>
            <TypewriterComponent
              onInit={(typewriter) => {
                typewriter
                  .changeDelay(75)
                  .changeDeleteSpeed(75)
                  .typeString("Hadiya Ali")
                  .changeDelay(75)
                  .changeDeleteSpeed(75)
                  .pauseFor(1000)
                  .deleteAll()
                  .typeString("하디야 알리")
                  .changeDelay(75)
                  .changeDeleteSpeed(75)
                  .pauseFor(1000)
                  .deleteAll()
                  .typeString("هدية علي")
                  .start();
              }}
            />
          </p>
          <BarLoader
            color="#000"
            cssOverride={{
              position: "fixed",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              transition: "width 0.5s ease-out",
            }}
            speedMultiplier={1.2}
            width={barWidth}
            loading={loading}
            height={1}
          />
        </>
      ) : (
        <div className={css.reveal} onAnimationEnd={() => setShowHello(true)}>
          {showHello && <p>hello</p>}
        </div>
      )}
    </div>
  );
}

export default App;
