import "./App.css";
import React, { useEffect, useState } from "react";
import BarLoader from "react-spinners/BarLoader";

function App() {
  const [loading, setLoading] = useState(false);
  const [barWidth, setBarWidth] = useState(150);
  const [text, setText] = useState("Hadiya Ali");

  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setText("Ali Hadiya");
      setTimeout(() => {
        setBarWidth(window.innerWidth);
      }, 1500);
    }, 8000);
  }, []);

  return (
    <div>
      {loading ? (
        <>
          <div
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              backgroundColor: "#000",
              opacity: 1,
              transition: "opacity 0.5s ease-in",
            }}
          ></div>
          <p
            style={{
              position: "fixed",
              top: "45%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              zIndex: "9999",
              opacity: 1,
              transition: "opacity 0.5s ease-in",
            }}
          >
            {text}
          </p>
          <BarLoader
            color="#000"
            cssOverride={{
              position: "fixed",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              transition: "width 0.5s ease-out",
            }}
            speedMultiplier={1.2}
            width={barWidth}
            loading={loading}
            height={1}
          />
        </>
      ) : (
        <>
          <div
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              backgroundColor: "#000",
              opacity: 1,
              transition: "opacity 0.5s ease-out",
            }}
          ></div>
          <p
            style={{
              position: "fixed",
              top: "45%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              zIndex: "9999",
              color: "#000",
              opacity: 0,
              transition: "opacity 0.5s ease-out",
            }}
          >
            {text}
          </p>
          <div
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              backgroundColor: "#000",
              opacity: 0,
              transition: "opacity 0.5s ease-out",
            }}
          ></div>
          <p
            style={{
              position: "fixed",
              top: "45%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              zIndex: "9999",
              color: "#fff",
              opacity: 1,
              transition: "opacity 0.5s ease-out",
            }}
          >
            hello
          </p>
        </>
      )}
    </div>
  );
}

export default App;
