import "./App.css";
import React, { useEffect, useState } from "react";
import BarLoader from "react-spinners/BarLoader";

function App() {
  const [loading, setLoading] = useState(false);
  const [barWidth, setBarWidth] = useState(150);
  const [nameIndex, setNameIndex] = useState(0);
  const names = ["Hadiya Ali", "هدية علي", "하디야 알리", "ہادیہ علی"];

  useEffect(() => {
    setLoading(true);
    const interval = setInterval(() => {
      setNameIndex((nameIndex + 1) % names.length);
    }, 1000);
    setTimeout(() => {
      setLoading(false);
      clearInterval(interval);
      setTimeout(() => {
        setBarWidth(150);
      }, 7000);
    }, 8000);
    setTimeout(() => {
      setBarWidth(window.innerWidth);
    }, 7400);
  }, [nameIndex, names.length]);

  return (
    <div>
      {loading ? (
        <>
          <p
            style={{
              position: "fixed",
              top: "45%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              zIndex: "9999",
            }}
          >
            {names[nameIndex]}
          </p>
          <BarLoader
            color="#000"
            cssOverride={{
              position: "fixed",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              transition: "width 0.5s ease-out",
            }}
            speedMultiplier={1.2}
            width={barWidth}
            loading={loading}
            height={1}
          />
        </>
      ) : (
        <p>hello</p>
      )}
    </div>
  );
}

export default App;
