import "./App.css";
import React, { useEffect, useState } from "react";
import BarLoader from "react-spinners/BarLoader";

function App() {
  const [loading, setLoading] = useState(false);
  const [barWidth, setBarWidth] = useState(300);
  const [showScreen, setShowScreen] = useState(false);

  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setTimeout(() => {
        setBarWidth(150);
      }, 7000);
    }, 8000);
    setTimeout(() => {
      setBarWidth(window.innerWidth);
    }, 7400);
    setTimeout(() => {
      setShowScreen(true);
    }, 8000);
  }, []);

  return (
    <div>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundColor: "black",
          opacity: showScreen ? 1 : 0,
          transition: "opacity 0.3s ease-in-out",
          zIndex: "9998",
        }}
      ></div>

      {loading ? (
        <>
          <p
            style={{
              position: "fixed",
              top: "45%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              zIndex: "9999",
            }}
          >
            Hadiya Ali
          </p>
          <BarLoader
            color="#000"
            cssOverride={{
              position: "fixed",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              transition: "width 0.5s ease-out",
            }}
            speedMultiplier={1}
            width={barWidth}
            loading={loading}
            height={1}
          />
        </>
      ) : (
        <p>hello</p>
      )}
    </div>
  );
}
